import React, { useState } from "react";

const CreateArea = ({data, setData}) => {

  const [newNote, setNewNote] = useState({
    newTitle: "",
    newContent: ""
  });

  const handleChange = (event) => {
    const {name, value} = event.target;
    // console.log(value);
    setNewNote({[name]: value});
  }

  const onAddNote = (event) => {
    event.preventDefault();
    const newInput = {
      title: newTitle,
      content: newContent,
      id: data.length + 0
    }
    setData(data.concat(newInput));
    // setNewTitle("");
    // setNewContent("");
  }

  return (
    <div>
      <form onSubmit={onAddNote}>
        <input name="newTitle" onChange={handleChange} value={newNote.newTitle} placeholder="Title" />
        <textarea name="newContent" onChange={handleChange} value={newNote.newContent} placeholder="Take a note..." rows="3" />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
